use super::prelude::Context;
use super::card::Card;
use super::cards::Cards;
use super::crib::Crib;
use super::cuts::Cuts;

use crate::domain::CARDS_DISCARDED_TO_CRIB;
use crate::services::prelude::discard;
use crate::view::{CardSlot, Cuts, Game as GameView, Hands, Role, Scores, Score};

use leptos::*;
use style4rs::style;

use std::ops::Range;

#[component]
pub fn Game(
    #[prop()]
    game: GameView,
) -> impl IntoView {
    let class = style!  {
        div {
            display: flex;
            flex-direction: row;
            justify-content: space-around;
        }
    };

    provide_context(game);
    
    view! {
        class = class,
        <div>
            <Scoreboard />
            <PlayArea />
            <CribAndCut />
        </div>
    }
}

#[component]
fn Scoreboard() -> impl IntoView {
    let game = use_context::<GameView>().unwrap();
    let scores = game.scores();
    provide_context(scores.clone());

    let default_score = Score::default();
    let current_player_score = scores.get(&Role::CurrentPlayer).unwrap_or(&default_score);
    let opponent_score = scores.get(&Role::Opponent).unwrap_or(&default_score);

    view! {
        <div>
            <svg height="90vh" viewBox="0 0 84 314">
                <defs>
                    <pattern id="woodPattern" width="80" height="80" patternUnits="userSpaceOnUse">
                        <image x="0" y="0" width="80" height="80" href="data:image/png;base64,"  />
                    </pattern>
                </defs>
                <rect width="100%" height="100%" rx="5" ry="5" fill="goldenrod" />
                <g transform="translate(2,2)">
                    <rect width="80" height="310" rx="4" ry="4" fill="url(#woodPattern)" />
                    <text x="10" y="15" font-size="7" fill="palegoldenrod">"You: "{current_player_score.front_peg()}</text>
                    <Track x_offset=10 y_offset=27 role=Role::CurrentPlayer />
                    <Track x_offset=50 y_offset=27 role=Role::Opponent />
                    <text x="10" y="300" font-size="7" fill="palegoldenrod">"Opponent: "{opponent_score.front_peg()}</text>
                </g>
            </svg>
        </div>
    }
}

#[component]
fn Track(
    #[prop()]
    x_offset: usize,
    
    #[prop()]
    y_offset: usize,

    #[prop()]
    role: Role
) -> impl IntoView {

    provide_context(role);

    let translate = format!("translate({},{})", x_offset, y_offset);

    view! {
        <g transform=translate>
            {move || (0..6).map(|n| { 
                let up_base = n*5+1;
                let up_range = up_base..(up_base+5);
                let down_base = 5*n+31;
                let down_range = down_base..(down_base+5);
                view!{ <Block x_offset=0 y_offset={n*42} up_range=up_range down_range=down_range /> }
            }).collect::<Vec<_>>()}
        </g>
    }
}

#[component]
fn Block(
    #[prop()]
    x_offset: usize,
    
    #[prop()]
    y_offset: usize,

    #[prop()]
    up_range: Range<usize>,
    
    #[prop()]
    down_range: Range<usize>) -> impl IntoView {

    let translate = format!("translate({},{})", x_offset, y_offset);

    let zipped = up_range.zip(down_range.rev()).enumerate();

    view!{
        <g transform=translate>
            <rect width="20" height="44" rx="3" ry="3" fill="goldenrod" />
            <g transform="translate(2,2)">
                <rect width="16" height="40" rx="2" ry="2" fill="palegoldenrod" />
                <g transform="translate(2,2)">
                    {zipped.map(|(i, (up, down))| view!{
                        <Hole x_offset=0 y_offset={8*i} representation={up} />
                        <Hole x_offset=8 y_offset={8*i} representation={down} />
                    }).collect::<Vec<_>>()}
                </g>
            </g>
        </g>}
}

#[component]
fn Hole(
    #[prop()]
    x_offset: usize,

    #[prop()]
    y_offset: usize,

    #[prop()]
    representation: usize
) -> impl IntoView {
    let role = use_context::<Role>().unwrap();
    let scores = use_context::<Scores>().unwrap();
    let default_score = Score::default();
    let score = scores.get(&role).unwrap_or(&default_score);

    let colour = (if role == Role::CurrentPlayer { "green" } else { "red" }).to_string();
    let fill = match representation {
        0 => colour,
        n if score.front_peg() % 60 == n => colour,
        n if score.back_peg() % 60 == n => colour,
        n if n >= 121 => colour,
        _ => "gray".into(),
    };

    let translate = format!("translate({},{})", x_offset, y_offset);

    view! {
        <g transform=translate>
            <circle cx="2" cy="2" r="2" fill=fill />
        </g>
    }
}

#[component]
fn PlayArea() -> impl IntoView {
    let game = use_context::<GameView>().unwrap();

    match game {
        GameView::Starting(cuts) => starting_play_area(&cuts).into_view(),
        GameView::Discarding(_, hands, _, _) => discarding_play_area(&hands).into_view(),
    }
}

fn starting_play_area(cuts: &Cuts) -> impl IntoView {
    view!{
        <Cuts player_cut={cuts[&Role::CurrentPlayer]} opponent_cut={cuts[&Role::Opponent]} />
    }
}

fn discarding_play_area(hands: &Hands) -> impl IntoView {
    let class = style!{
        div {
            display: flex;
            flex-direction: column;
            justify-content: space-around;
            align-items: center;
        }
    };

    let current_player_cards = hands[&Role::CurrentPlayer].clone();
    let opponent_cards = hands[&Role::Opponent].clone();
    let viewable_player_cards = current_player_cards.clone();

    let (selected, set_selected) = create_signal(Vec::<bool>::new());
    let selected_count = move || selected().iter().filter(|s| **s).count();
    let disabled = move || selected_count() != CARDS_DISCARDED_TO_CRIB;
    let selected_cards = move || {
        let cards = current_player_cards.clone();
        selected()
            .into_iter()
            .zip(cards)
            .filter_map(|(s, c)| {
                if let CardSlot::FaceUp(card) = c {
                    s.then_some(card)
                } else {
                    None
                }
        })
        .collect::<Vec<_>>()
    };

    let context = use_context::<Context>().unwrap();

    let on_discard = {
        let context = context.clone();
        move |_| {
            let id = context.id.clone();
            let state = context.state;
            let cards = selected_cards().clone();
            spawn_local(async move {
                if let Ok(game) = discard(id, cards).await {
                    state.set(Some(game.clone()));
                }
            });
        }
    };

    view! {
        class = class,
        <div>
            {

            }
            <div>
                <Cards cards=viewable_player_cards on_selected=set_selected />
                <span><button on:click=on_discard disabled=disabled>"Discard"</button></span>
            </div>
            <div />
            <div><Cards cards=opponent_cards /></div>
        </div>
    }
}

#[component]
fn CribAndCut() -> impl IntoView {
    let class = style!{
        div {
            display: flex;
            flex-direction: column;
            justify-content: space-between;
        }
    };

    let game = use_context::<GameView>().unwrap();

    let dealer = game.dealer().unwrap_or(Role::CurrentPlayer);
    let crib = game.crib();
    let cut = game.cut();

    let empty_view = view! { <Card card=CardSlot::Empty /> };
    let crib_view = view! { <Crib crib=crib stacked=true /> };

    view! {
        class = class,
        <div>
            {if dealer == Role::CurrentPlayer { crib_view.clone() } else { empty_view.clone() }}
            <Card card=cut />
            {if dealer == Role::Opponent { crib_view } else { empty_view }}
        </div>
    }
}
